<?php
// Функция быстрой сортировки
function quickSort($arr) {
    if (count($arr) < 2) {
        return $arr; // Базовый случай: если массив из 0 или 1 элемента, он уже отсортирован
    }
    $pivot = $arr[0]; // Берем первый элемент как опорный
    $left = []; // Массив для элементов меньше опорного
    $right = []; // Массив для элементов больше опорного
    for ($i = 1; $i < count($arr); $i++) {
        if ($arr[$i] < $pivot) {
            $left[] = $arr[$i]; // Добавляем в левый массив
        } else {
            $right[] = $arr[$i]; // Добавляем в правый массив
        }
    }
    return array_merge(quickSort($left), [$pivot], quickSort($right)); // Рекурсивно сортируем части и объединяем
}

// Пример использования
$arr = [10, 7, 8, 9, 1, 5];
print_r(quickSort($arr));
