<?php
// Функция сортировки пузырьком
function bubbleSort($arr) {
    $n = count($arr); // Определяем длину массива
    for ($i = 0; $i < $n - 1; $i++) { // Внешний цикл проходит по массиву
        for ($j = 0; $j < $n - $i - 1; $j++) { // Внутренний цикл сравнивает пары элементов
            if ($arr[$j] > $arr[$j + 1]) { // Если текущий элемент больше следующего, меняем их местами
                $temp = $arr[$j];
                $arr[$j] = $arr[$j + 1];
                $arr[$j + 1] = $temp;
            }
        }
    }
    return $arr;
}

// Пример использования
$arr = [64, 34, 25, 12, 22, 11, 90];
print_r(bubbleSort($arr));
